jQuery(document).ready(function($){jQuery(window).scroll(function(){if(jQuery(this).scrollTop()>300){jQuery('.header_fixed #header').addClass('scrolled');}else{jQuery('.header_fixed #header').removeClass('scrolled');}});$('.tmnf_menu > li.menu-item-has-children').prepend('<span class="toggle_submenu">&#9660;</span>');$('.tmnf_menu .toggle_submenu').on('click',function(){$(this).toggleClass('tmnf_submenu_active');$(this).siblings('ul').slideToggle();});$('.tmnf_menu li').on('keydown',function(e){if(e.which===13||e.which===32){$(this).children('ul').toggle();}});$(".tmnf_top_scroll").hide();$(function(){$(window).scroll(function(){if($(this).scrollTop()>150){$('.tmnf_top_scroll').fadeIn();}else{$('.tmnf_top_scroll').fadeOut();}});$('.tmnf_top_scroll').on('click',function(){$('html, body').animate({scrollTop:0},300);return false;});});var alterClass=function(){var ww=document.body.clientWidth;if(ww<1200){jQuery('body').addClass('tmnf_respo_magic');}else if(ww>=1200){jQuery('body').removeClass('tmnf_respo_magic');};};jQuery(window).resize(function(){alterClass();});alterClass();});document.addEventListener("DOMContentLoaded",function(){if(document.querySelector('.tmnf_eleslider')){setTimeout(function(){document.querySelector('.tmnf_eleslider').classList.add('active-initialized');},200);}});